import React, { useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './EditPost.css';
import { supabase } from '../client';

const EditPost = ({ data }) => {
  const { id } = useParams();
  const post = data.filter((item) => item.id === id)[0];

  const [title, setTitle] = useState(post.title);
  const [author, setAuthor] = useState(post.author);
  const [description, setDescription] = useState(post.description);

  const history = useHistory();

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleAuthorChange = (event) => {
    setAuthor(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    await supabase.from('Posts').update({ title, author, description }).eq('id', id);

    history.push('/');
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label> <br />
        <input type="text" id="title" name="title" value={title} onChange={handleTitleChange} /><br />
        <br />

        <label htmlFor="author">Author</label><br />
        <input type="text" id="author" name="author" value={author} onChange={handleAuthorChange} /><br />
        <br />

        <label htmlFor="description">Description</label><br />
        <textarea
          rows="5"
          cols="50"
          id="description"
          value={description}
          onChange={handleDescriptionChange}
        />
        <br />
        <input type="submit" value="Submit" />
        <button className="deleteButton">Delete</button>
      </form>
    </div>
  );
};

export default EditPost;
